def unique(listoflists):
    tx = [tuple(s) for s in listoflists]
    ts = list(set(tx))
    return [list(t) for t in ts]

def round_100(x):
    prefix = x // 100
    rm = int(str(x)[-2:])
    if rm >= 50:
        prefix += 1
    return prefix

