        def cleanup_stw(n):
            del app.vis_dict[n]
            app.canvas.delete(n)
        def stw_loop(ents):
            if ents == []:
                self.finish_strength_through_wounding()
            else:
                id = ents[0]
                app.get_focus(id)
                ents = ents[1:]
                ent = app.ent_dict[id]
                s = ent.loc[:]
                u = 'Strength_Through_Wounding' + str(app.count) # not an effect, just need unique int
