        lock(Bot.ai_normal_move, self, last)
        ents = [v for k,v in app.all_ents().items() if dist(v.loc,self.loc)==1]
        def charge_loop(ents):
            if ents == []:
                self.cancel_charge()
            else:
                ent = ents[0]
                ents = ents[1:]
                app.vis_dict['Lacerate'] = Vis(name = 'Lacerate', loc = ent.loc[:])
                my_agl = self.get_abl('agl')
