        app.canvas.create_text(loc[0]*100+49-app.moved_right, loc[1]*100+64-app.moved_down, text = '+'+str(amt)+' str, end, agl, spirit', justify = 'center', font = ('chalkduster', 13), fill = 'black', tags = 'text')
