        # +/- buttons linked to Label
        app.tmp = tk.IntVar()
        app.tmp.set(0)
        def plus():
            cur = app.tmp.get()
            if self.magick > cur:
                app.tmp.set(cur+1)
        p = partial(plus)
        b = tk.Button(app.context_menu, text = '+', wraplength = 190, font = ('chalkduster', 22), fg = 'indianred', highlightbackground = 'tan3', command = p)
        b.pack(side = 'top', pady = 2)
