                name = smn[0]
                cost = smn[1]
                func = partial(depress)
                b1 = tk.Button(self.summon_frame, text = name.replace('_',' ') + ' '+str(cost), wraplength = 190, font = ('chalkduster', 17), fg='indianred', highlightbackground = 'tan3')
