        self.close = tk.Button(self.help_popup, text = 'Close', font = ('chalkduster', 22), fg='tan3', highlightbackground = 'tan3', command = lambda win = self.help_popup : self.destroy_release(win))
        self.close.pack()
        self.text.pack()
        sb.pack(side = 'right', fill = 'y')
